import React from 'react';
import Slider from 'react-slick'; // Импортируем компонент Slider
import ReviewItem from '../components/ReviewItem';
import ChatWindow from '../components/ChatWindow';
import '../styles/ReviewsPage.css';

const ReviewsPage = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
  };

  const reviews = [
    {
      id: 1,
      image: 'path_to_image',
      text: 'Отличный сервис!',
      author: 'Иван Иванов'
    },
    {
      id: 2,
      image: 'path_to_image',
      text: 'Все было вкусно!',
      author: 'Петр Петров'
    },
    // Дополнительные отзывы...
  ];

  return (
    <div className="reviews-page">
      <h2>Отзывы клиентов</h2>
      <Slider {...settings}>
        {reviews.map((review) => (
          <ReviewItem key={review.id} review={review} />
        ))}
      </Slider>
      <h2>Общение с менеджером</h2>
      <ChatWindow />
    </div>
  );
}

export default ReviewsPage;
