import React, { useState } from 'react';
import Slider from 'react-slick';
import ReviewItem from '../components/ReviewItem';
import ChatWindow from '../components/ChatWindow';
import '../styles/ReviewsPage.css';

const ReviewsPage = () => {
  const [chatVisible, setChatVisible] = useState(false);

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
    nextArrow: <SampleNextArrow />,
    prevArrow: <SamplePrevArrow />
  };

  const reviews = [
    {
      id: 1,
      image: 'path_to_image',
      avatar: 'path_to_avatar',
      text: 'Отличный сервис!',
      author: 'Иван Иванов'
    },
    {
      id: 2,
      image: 'path_to_image',
      avatar: 'path_to_avatar',
      text: 'Все было вкусно!',
      author: 'Петр Петров'
    },
    // Дополнительные отзывы...
  ];

  return (
    <div className="reviews-page">
      <Slider {...settings} className="full-page-slider">
        {reviews.map((review) => (
          <ReviewItem key={review.id} review={review} />
        ))}
      </Slider>
      <button className="chat-toggle" onClick={() => setChatVisible(!chatVisible)}>Чат с менеджером</button>
      {chatVisible && (
        <div className="chat-popup">
          <button className="chat-close" onClick={() => setChatVisible(false)}>×</button>
          <ChatWindow />
        </div>
      )}
    </div>
  );
}

function SampleNextArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={`${className} custom-arrow custom-next-arrow`}
      style={{ ...style, display: 'block' }}
      onClick={onClick}
    />
  );
}

function SamplePrevArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={`${className} custom-arrow custom-prev-arrow`}
      style={{ ...style, display: 'block' }}
      onClick={onClick}
    />
  );
}

export default ReviewsPage;
