import React from 'react';
import '../styles/DrinkItem.css';

function DrinkItem({ name, description, price }) {
  return (
    <div className="drink-item">
      <h3>{name}</h3>
      <p>{description}</p>
      <p>Цена: {price} руб.</p>
    </div>
  );
}

export default DrinkItem;
