import React, { useState } from 'react';
import '../styles/ReviewItem.css';

const ReviewItem = ({ review }) => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const nextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex === review.images.length - 1 ? 0 : prevIndex + 1));
  };

  const prevImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex === 0 ? review.images.length - 1 : prevIndex - 1));
  };

  return (
    <div className="review-item">
      <img src={review.images[currentImageIndex]} alt="Review" className="review-image" />
      <div className="review-info">
        <p className="review-text">{review.text}</p>
        <p className="review-author">{review.author}</p>
      </div>
      <div className="image-navigation">
        <button onClick={prevImage}>Previous</button>
        <button onClick={nextImage}>Next</button>
      </div>
    </div>
  );
};

export default ReviewItem;
